<script>
$(document).ready(function() {

    $('#createProductForm').on('submit', function(event) {
        event.preventDefault();
        $.ajax({
            type: 'POST',
            url: '/path/to/create/product',
            data: {
                name: $('#productName').val(),
                price: $('#productPrice').val()
            },
            success: function(response) {
                $('#main-content').html(response);
                
            }
        });
    });

    $('#updateProductForm').on('submit', function(event) {
        event.preventDefault();
        $.ajax({
            type: 'POST',
            url: '/path/to/update/product',
            data: {
                id: $('#updateProductId').val(),
                name: $('#updateProductName').val(),
                price: $('#updateProductPrice').val()
            },
            success: function(response) {
                $('#main-content').html(response);
                
            }
        });
    });

    $('#deleteProductForm').on('submit', function(event) {
        event.preventDefault();
        $.ajax({
            type: 'POST',
            url: '/path/to/delete/product',
            data: {
                id: $('#deleteProductId').val()
            },
            success: function(response) {
                $('#main-content').html(response);
                
            }
        });
    });
});
</script>